CREATE TABLE `m_pocket_apply` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `user_id` int(10) unsigned NOT NULL COMMENT '用户编号',
  `phone` char(13) NOT NULL COMMENT '手机号',
  `true_name` varchar(255) NOT NULL COMMENT '真实姓名',
  `id_card` varchar(30) NOT NULL COMMENT '身份证号',
  `card_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '证件类型 1:身份证 2:其他证件',
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1:已申请 2:已通过 3:未通过',
  `info_refuse` varchar(255) NOT NULL COMMENT '拒绝原因',
  `invite_id` tinyint(6) unsigned NOT NULL DEFAULT '0' COMMENT '邀请编号',
  `invite_type` varchar(20) NOT NULL COMMENT '申请类型',
  `zhuanyuan` varchar(20) DEFAULT NULL COMMENT '专员',
  `tag` varchar(20) DEFAULT NULL COMMENT '标签',
  `department` varchar(40) NOT NULL COMMENT '部门',
  `leixing` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '1:当天 2:当月',
  `money_total` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '累计收入(预计)',
  `money_fetch` decimal(10,2) NOT NULL COMMENT '已提金额',
  `money_total_valid` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '累计收入',
  `order_amount` decimal(10,2) NOT NULL COMMENT '交易额',
  `order_sum` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '交易量',
  `postdate` int(10) NOT NULL DEFAULT '0' COMMENT '时间',
  `postdate_confirm` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '审核通过时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

