INSERT INTO `site_html_to_sql` VALUES (3, 'sdf', '\r\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n<title></title>\r\n<meta http-equiv="Content-Type" content="text/html; charset=utf-8">\r\n<META NAME="ROBOTS" CONTENT="NOINDEX, NOFOLLOW">\r\n\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/bootstrap_min.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/bootstrap_responsive_min.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/style.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/todc_bootstrap_button.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/daterangepicker.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/inside.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/chosen.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/css.css">\r\n<link rel="stylesheet" href="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/themes/default/default.css" />\r\n<script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/kindeditor-min.js"></script>\r\n<script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/lang/zh_CN.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jQuery.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_ui_widget_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/bootstrap_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_form_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_validate_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_validate_methods.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_form_wizard_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/daterangepicker.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/moment_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/inside.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/common.js"></script>\r\n\r\n</head>\r\n<body><script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/kindeditor.config-upfile.js"></script>\r\n<div id="main">\r\n        <div class="container-fluid">\r\n            <div class="row-fluid">\r\n                <div class="span12">\r\n                    <div class="box">\r\n                        \r\n  <form action="" method="get" class="right_tit2">\r\n    发布通知\r\n    <input name="" type="button" class="input1" value="返回" onclick="Javascript:window.history.go(-1)">\r\n  </form>\r\n                        <div class="rightsum">\r\n  <div class="span12">\r\n    <div class="box">\r\n      <div class="box-content">\r\n        <form action="" method="post" class="form-horizontal form-validate wj_form">\r\n          <div class="control-group">\r\n            <label for="keyword" class="control-label">标题：</label>\r\n            <div class="controls">\r\n              <input type="text" name="title" id="short_title" class="input-large" data-rule-required="true" data-rule-maxlength="30" value="" />\r\n              <span class="help-inline">不超过30个字符</span> </div>\r\n          </div>\r\n          <div class="control-group">\r\n                                    	<label class="control-label" for="cover">通知</label>\r\n                                    	<div class="controls">\r\n                                        	                                            	<img id="thumb_img" src="http://localhost/codeIgniter_weixin3/resource/images/member_center/guajiang_10.gif" style="max-height:100px;" />\r\n                                            	<input id="pic_cover" type="hidden" name="pic_cover" value="http://localhost/codeIgniter_weixin3/resource/images/member_center/guajiang_10.gif" class="input-xlarge" readonly="readonly" data-rule-required="true" data-rule-url="true" />\r\n                                        	                                        	<span class="help-inline" style="margin-top:3px;">\r\n                                            	\r\n												<a class="wj_btn6" id="insertimage">上传图片</a> 建议尺寸:宽200px,高200px\r\n                                        	</span>\r\n                                    	</div>\r\n                                	</div>\r\n          <div class="control-group">\r\n            <label class="control-label">选择人群</label>\r\n            <div class="controls">\r\n              <label class="radio inline">\r\n              <input type="radio" name="to_grade" value="0" checked="checked" />\r\n              所有会员 </label>\r\n            </div>\r\n          </div>\r\n          <script src="/templates/kindeditor/kindeditor-min.js"></script>\r\n          <div class="control-group">\r\n            <label class="control-label" for="content">图文详细页内容</label>\r\n            <div class="controls">\r\n              <textarea class="input-large" name="info" id="info" data-rule-required="true"  ></textarea>\r\n            </div>\r\n          </div>\r\n          <div class="form-actions">\r\n         \r\n            <button id="bsubmit" type="submit" data-loading-text="提交中..." class="btn btn-primary">保存</button>\r\n            <button type="button" class="btn" onClick="Javascript:window.history.go(-1)">取消</button>\r\n          </div>\r\n        </form>\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>\r\n\r\n                        <script>\r\n\r\n                        var seting = {\r\n                                themeType: "simple",\r\n                                resizeType: 1,\r\n                                syncType:"",\r\n                                allowPreviewEmoticons: false,\r\n                                items: [\r\n        ''source'', ''undo'', ''redo'', ''plainpaste'', ''plainpaste'', ''wordpaste'', ''clearhtml'', ''quickformat'', ''selectall'', ''fullscreen'', ''fontname'', ''fontsize'', ''|'', ''forecolor'', ''hilitecolor'', ''bold'', ''italic'', ''underline'', ''hr'',\r\n        ''removeformat'', ''|'', ''justifyleft'', ''justifycenter'', ''justifyright'', ''insertorderedlist'',\r\n        ''insertunorderedlist'', ''|'', ''emoticons'', ''image'', ''link'', ''unlink'', ''baidumap''],\r\n                                allowFileManager: true,\r\n                                minWidth: 400,\r\n                                width: 600,\r\n                                afterCreate: function () {\r\n                                    this.sync();\r\n                                },\r\n                                afterBlur: function () {\r\n                                    this.sync();\r\n                                }\r\n                            }\r\n							\r\n						\r\n                            KindEditor.ready(function (K) {\r\n                                var editor1 = K.create(''#info'', seting);\r\n                                K(''a.insertimage'').click(function (e) {\r\n                                    editor1.loadPlugin(''smimage'', function () {\r\n                                        editor1.plugin.imageDialog({\r\n                                            imageUrl: $(e.target).parent().prev().val(),\r\n                                            clickFn: function (url, title, width, height, border, align) {\r\n                                                $(e.target).parent().prev().val(url);\r\n												$(e.target).parent().next().find("img").attr("src",url);\r\n                                                editor1.hideDialog();\r\n                                            }\r\n                                        });\r\n										\r\n                                    });\r\n									\r\n                                });\r\n                            });\r\n                        </script>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>', '', 'CREATE TABLE IF NOT EXISTS `site_1394517840` (\r\n`id` int(11) NOT NULL auto_increment COMMENT ''id'',\r\n`title` varchar(200) NOT NULL COMMENT ''title'',\r\n`pic_cover` varchar(200) NOT NULL COMMENT ''pic_cover'',\r\n`to_grade` varchar(200) NOT NULL COMMENT ''to_grade'',\r\n`info` varchar(200) NOT NULL COMMENT ''info'',\r\n PRIMARY KEY  (`id`)\r\n) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1\r\n\r\n\r\n', 1394517840);
INSERT INTO `site_html_to_sql` VALUES (4, '商家设置', '\r\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml">\r\n<head>\r\n<title></title>\r\n<meta http-equiv="Content-Type" content="text/html; charset=utf-8">\r\n<META NAME="ROBOTS" CONTENT="NOINDEX, NOFOLLOW">\r\n\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/bootstrap_min.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/bootstrap_responsive_min.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/style.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/todc_bootstrap_button.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/daterangepicker.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/inside.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/chosen.css" media="all">\r\n<link rel="stylesheet" type="text/css" href="http://localhost/codeIgniter_weixin3/resource/css/member_center/css.css">\r\n<link rel="stylesheet" href="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/themes/default/default.css" />\r\n<script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/kindeditor-min.js"></script>\r\n<script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/lang/zh_CN.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jQuery.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_ui_widget_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/bootstrap_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_form_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_validate_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_validate_methods.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/jquery_form_wizard_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/daterangepicker.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/moment_min.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/inside.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/common.js"></script>\r\n\r\n</head>\r\n<body><script charset="utf-8" src="http://localhost/codeIgniter_weixin3/resource/kindeditor-4.1.9/kindeditor.config-upfile.js"></script>\r\n<script type="text/javascript" src="http://localhost/codeIgniter_weixin3/resource/js/member_center/map.js"></script>\r\n<script src="http://api.map.baidu.com/api?key=24ffad3855e675265336a4cfb46d32b4&v=1.1&services=true" type="text/javascript"></script>\r\n<div id="main">\r\n        <div class="container-fluid">\r\n            <div class="row-fluid">\r\n                <div class="span12">\r\n                    <div class="box">\r\n                        \r\n  <form action="" method="get" class="right_tit2">\r\n    商户设置\r\n    <input name="" type="button" class="input1" value="返回" onclick="Javascript:window.history.go(-1)">\r\n  </form>\r\n                        <div class="rightsum">\r\n                          <div class="span12">\r\n                        <div class="box">\r\n                            \r\n                            <div class="box-content">\r\n                                <form action="" method="POST" class=''form-horizontal form-validate wj_form''>\r\n									 <input type="hidden" name="pid" value=""/>\r\n	                                	                                	\r\n	                                	                                \r\n                                    <div class="control-group">\r\n                                        <label class="control-label" for="keyword">商户名称</label>\r\n                                        <div class="controls">\r\n                                            <input type="text" name="shop_name" id="shop_name" value="x1a" data-rule-required="true" data-rule-maxlength="50" />\r\n                                            <span>*</span>\r\n                                            <span class="help-inline">最多只能输入50个字符。</span>\r\n                                        </div>\r\n                                    </div>\r\n                                    \r\n                                    <div class="control-group">\r\n                                        <label class="control-label" for="keyword">触发关键词</label>\r\n                                        <div class="controls">\r\n                                            <input type="text" name="keyword" id="keyword" value="x2b" data-rule-required="true" data-rule-maxlength="20" />\r\n                                            <span>*</span>\r\n                                            <span class="help-inline">用户输入此“关键词”将可以触发会员卡图文，最多只能输入20个字符。</span>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class="control-group">\r\n                                    	<label class="control-label" for="cover">图文消息封面</label>\r\n                                    	<div class="controls">\r\n                                        	                                            	<img id="thumb_img" src="/codeIgniter_weixin3/resource/kindeditor-4.1.9/attached/image/20140221/20140221060241_42044.gif" style="max-height:100px;" />\r\n                                            	<input id="pic_cover" type="hidden" name="pic_cover" value="/codeIgniter_weixin3/resource/kindeditor-4.1.9/attached/image/20140221/20140221060241_42044.gif" class="input-xlarge" readonly="readonly" data-rule-required="true" data-rule-url="true" />\r\n                                        	                                        	<span class="help-inline" style="margin-top:3px;">\r\n                                            	\r\n												<a class="wj_btn6" id="insertimage">上传图片</a> 建议尺寸:宽175px,高100px\r\n                                        	</span>\r\n                                    	</div>\r\n                                	</div>\r\n                                	<div class="control-group">\r\n                                        <label for="textfield" class="control-label">\r\n                                                    封面消息\r\n                                        </label>\r\n                                        <div class="controls">\r\n                                            <textarea class="input-large" name="info" id="info" data-rule-required="true" style="width:658px;height:80px;" data-rule-maxlength="140">x3c</textarea>\r\n                                        	<span>*</span> <span class="help-inline">最多只能输入140个字符。</span>\r\n                                        </div>\r\n                                    </div>\r\n                                    <div class="control-group">\r\n        