-- 
-- 表的结构 `site_module`
-- 

CREATE TABLE `site_module` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(20) NOT NULL,
  `url` varchar(100) CHARACTER SET gbk NOT NULL,
  `leixing` varchar(100) DEFAULT NULL,
  `data` mediumtext COMMENT '数据',
  `data_front_style` mediumtext NOT NULL COMMENT '前台jason',
  `data_enum` mediumtext NOT NULL COMMENT '多值输入',
  `is_first` tinyint(1) DEFAULT '0' COMMENT '第一次显示',
  `info_control` mediumtext NOT NULL COMMENT '控制器',
  `info_view` mediumtext NOT NULL COMMENT '视图',
  `create_view` tinyint(2) NOT NULL DEFAULT '0' COMMENT '生成类型',
  `laravel_timestamp` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '1:有时间戳 0 : 无',
  `template_site` varchar(100) DEFAULT NULL COMMENT '前台模板',
  `template_wap` varchar(100) DEFAULT NULL COMMENT '手机模板',
  `sort_order` int(3) DEFAULT '0',
  `postdate` int(10) unsigned DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  KEY `sort_order` (`sort_order`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=683 ;

