INSERT INTO `site_module` VALUES (452, '角色管理', 'manager_role', 'common', '{"id":["id","int","10","","","NOT NULL","","auto_increment","ID","","ID",false,"\\u8bf7\\u8f93\\u5165ID","input-xxlarge","2","1","2"],"name":["name","varchar","200","","","NOT NULL","","","\\u89d2\\u8272\\u540d\\u79f0","","\\u89d2\\u8272\\u540d\\u79f0",["require"],"\\u8bf7\\u8f93\\u5165\\u89d2\\u8272\\u540d\\u79f0","input-xxlarge","2","1","1"],"description":["description","varchar","800","","","NOT NULL","","","\\u89d2\\u8272\\u63cf\\u8ff0","","\\u89d2\\u8272\\u63cf\\u8ff0",false,"\\u8bf7\\u8f93\\u5165\\u89d2\\u8272\\u63cf\\u8ff0","input-xxlarge","2","1","1"],"is_show":["is_show","tinyint","1","","","NOT NULL","","","\\u72b6\\u6001","is_show","\\u72b6\\u6001",false,"\\u72b6\\u6001","input-xxlarge","2","1","1"],"role":["role","varchar","800","","","NOT NULL","","","\\u6743\\u9650\\u8bbe\\u7f6e","","\\u6743\\u9650\\u8bbe\\u7f6e",false,"\\u8bf7\\u8f93\\u5165\\u6743\\u9650\\u8bbe\\u7f6e","input-xxlarge","2","1","1"],"sort_order":["sort_order","int","10","","","NOT NULL","","","\\u6392\\u5e8f","sort_order","\\u6392\\u5e8f",["number"],"\\u8bf7\\u8f93\\u5165\\u6392\\u5e8f","input-xxlarge","2","1","1"]}', '', '', 0, '<pre class="prettyprint lang-html">/***生成小图***/\r\n			$pic_small=substr($pic,0,strrpos($pic,"."))."s".substr($pic,strrpos($pic,"."));\r\n			$sing=$this-&gt;image_moo-&gt;load($pic)-&gt;resize(100,100)-&gt;save($pic_small,true);\r\n			//@unlink($pic);\r\n			$i_array=array("pic"=&gt;$new_crop2,"pic_small"=&gt;$pic_small);</pre>\r\n<br />', '<pre class="prettyprint lang-js">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<pre class="prettyprint lang-html">	function button_next($id,$row2)\r\n	{\r\n		$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix.$this-&gt;table_name." where id&gt;? order by id asc limit 1",array($id));\r\n		$row =$result-&gt;row();\r\n		return ($row) ? $row : $row2[0];\r\n	}</pre>\r\n<pre class="prettyprint lang-html">function detail()\r\n	{\r\n		$this-&gt;load-&gt;database();\r\n		$id=$this-&gt;uri-&gt;segment(3);\r\n		if(preg_match("/^\\d+$/is",$id))\r\n		{\r\n			$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix.$this-&gt;table_name." where id=?",$id);\r\n			$row=$result-&gt;result();\r\n			if($row)\r\n			{\r\n				$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix."article_content where id=?",$id);\r\n				$row2=$result-&gt;result();\r\n				$info=array_merge($row,$row2);\r\n				$data["product_sale"]=get_table_list("product_sale","15","desc","id");\r\n				$data["product_site"]=get_table_list("product_site","10","desc","sort_order");\r\n				$data["product_brand"]=get_table_list("product_brand","20","desc","id");\r\n				$data["info"]=$info;\r\n				$data["next_button"]=$this-&gt;button_next($id,$row);\r\n				$data["pre_button"]=$this-&gt;button_pre($id,$row);\r\n				show("article_show",$data);\r\n			}\r\n			else\r\n			show_404();\r\n		}\r\n		else\r\n		{\r\n			show_404();\r\n		}\r\n	}</pre>\r\n<br />\r\n</pre>', 0, 0, NULL, NULL, 0, 1415183425, 1);
