table_name." where id&gt;? order by id asc limit 1",array($id));\r\n		$row =$result-&gt;row();\r\n		return ($row) ? $row : $row2[0];\r\n	}</pre>\r\n<pre class="prettyprint lang-html">function detail()\r\n	{\r\n		$this-&gt;load-&gt;database();\r\n		$id=$this-&gt;uri-&gt;segment(3);\r\n		if(preg_match("/^\\d+$/is",$id))\r\n		{\r\n			$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix.$this-&gt;table_name." where id=?",$id);\r\n			$row=$result-&gt;result();\r\n			if($row)\r\n			{\r\n				$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix."article_content where id=?",$id);\r\n				$row2=$result-&gt;result();\r\n				$info=array_merge($row,$row2);\r\n				$data["product_sale"]=get_table_list("product_sale","15","desc","id");\r\n				$data["product_site"]=get_table_list("product_site","10","desc","sort_order");\r\n				$data["product_brand"]=get_table_list("product_brand","20","desc","id");\r\n				$data["info"]=$info;\r\n				$data["next_button"]=$this-&gt;button_next($id,$row);\r\n				$data["pre_button"]=$this-&gt;button_pre($id,$row);\r\n				show("article_show",$data);\r\n			}\r\n			else\r\n			show_404();\r\n		}\r\n		else\r\n		{\r\n			show_404();\r\n		}\r\n	}</pre>\r\n<br />\r\n</pre>', 0, 0, NULL, NULL, 0, 1402656990, 1);
