INSERT INTO `site_module_type` VALUES (3, '页面管理', 'page', '[["id","int","10","","","NOT NULL","","auto_increment","ID",""],["title","varchar","200","","","NOT NULL","","","\\u6807\\u9898",""],["url","char","40","","","NOT NULL","","","url",""],["content","longtext","","","","NOT NULL","","","\\u5185\\u5bb9","editor"],["postdate","int","10","","","NOT NULL","","","\\u65f6\\u95f4","postdate"]]', 'function show()<br />\r\n{<br />\r\n$this-&gt;load-&gt;helper(''artilce'');<br />\r\n$row=page_show($this-&gt;uri-&gt;segment(2));<br />\r\n<br />\r\n$this-&gt;load-&gt;database();<br />\r\n$result=$CI-&gt;db-&gt;query("select * from ".$CI-&gt;db-&gt;dbprefix."page where url=?",$this-&gt;uri-&gt;segment(2));<br />\r\n$row=$result-&gt;result_array();<br />\r\nif(!$row)<br />\r\nshow_404();<br />\r\n$data_url=array("base_url"=&gt;base_url(),"web_url"=&gt;base_url()."site/index/","site_url"=&gt;site_url(),"curren_url"=&gt;$url);<br />\r\n$row[0][''page_left'']=$CI-&gt;load-&gt;view(''page_left'',$data_url, true);<br />\r\n$row=$row[0];<br />\r\n$data=array_merge($row,$this-&gt;seo());<br />\r\n$data[''header_nav'']=$row[''title''];<br />\r\n$data[''url2'']=$this-&gt;uri-&gt;segment(2);<br />\r\nshow("page_show",$data);<br />\r\n}<br />\r\nfunction seo()<br />\r\n{<br />\r\n$url=$this-&gt;uri-&gt;segment(2);<br />\r\nif(trim($url))<br />\r\n{<br />\r\n$this-&gt;load-&gt;database();<br />\r\n$result=$this-&gt;db-&gt;query("select * from ".$this-&gt;db-&gt;dbprefix."page where url=?",$url);<br />\r\n$row=$result-&gt;row();;<br />\r\nif(!$row)<br />\r\nshow_404();<br />\r\nreturn array("seo"=&gt;array("seo_title"=&gt;$row-&gt;title));<br />\r\n}<br />\r\n}<br />', '', 1391752407);
