  `habit` tinyint(1) NOT NULL COMMENT '习惯',
  `pinlv` tinyint(1) NOT NULL COMMENT '频率',
  `paibian` tinyint(1) NOT NULL COMMENT '排便情况',
  `shiyong` tinyint(2) NOT NULL COMMENT '是否在食用维您轻伊粉',
