  `eat_start` tinyint(2) NOT NULL COMMENT '开始食用体重',
  `weight_current` tinyint(2) NOT NULL COMMENT '目前体重',
  `yaowei_current` tinyint(2) NOT NULL COMMENT '目前腰围',
  `shiyong_ganshou` varchar(800) NOT NULL COMMENT '食用感受',
