  `isadmin` tinyint(1) NOT NULL COMMENT '是否为管理员',
  `starttime` int(10) NOT NULL COMMENT '开户时间',
  `level_id` tinyint(1) NOT NULL COMMENT '1:普通用户2:普通会员3:白金会员4：钻石会员',
  `mendtime` int(10) NOT NULL COMMENT '到期时间',
  `next_level_id` tinyint(1) DEFAULT '1' COMMENT '续费级别',
  `next_mendtime` int(10) NOT NULL COMMENT '到期时间',
  `address_p` int(11) NOT NULL COMMENT '省',
  `address_c` int(11) NOT NULL COMMENT '市',
  `address_a` int(11) NOT NULL COMMENT '区',
  `address` varchar(255) NOT NULL COMMENT '客户地址',
  `agentid` int(11) unsigned DEFAULT '0' COMMENT '代理id',
  `financeid` int(11) NOT NULL COMMENT '财务id',
  `customerid` int(11) NOT NULL COMMENT '客服id',
  `networksaleid` int(11) NOT NULL COMMENT '网销id',
  `ip` varchar(32) NOT NULL COMMENT 'ip地址',
  `staffid` int(11) NOT NULL COMMENT '员工id',
  `manager` varchar(50) NOT NULL COMMENT '客户经理',
  `linkman` varchar(50) NOT NULL COMMENT '联系人',
  `tel` varchar(50) NOT NULL COMMENT '电话',
  `isdirect` tinyint(1) NOT NULL COMMENT '是否直接客户',
  `isfromnet` tinyint(1) NOT NULL COMMENT '是否网络注册',
  `parentid` int(11) DEFAULT '0' COMMENT '上级用户id',
  `quota` smallint(6) DEFAULT '1' COMMENT '账户限额',
  `surplus_sms` int(11) DEFAULT '0' COMMENT '剩余短信条数',
  `state` tinyint(1) NOT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

